import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import { render } from '@testing-library/react';

class App extends Component{
  constructor(props) {
    super(props);
    this.DisparaClick = this.DisparaClick.bind(this);
    this.state={
      name:'Clodoaldo Pereira',
      counter: 0
    };
  }

  DisparaClick(){
    console.log("<p> clicado");
    this.setState({ name : 'C. Pereira' });
  }

  handleAClick = () => {
    const { counter } = this.state;
    this.setState ({ counter: counter++});
  }

  render(){
    const Nome = this.state.name;


    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload. OK?
          </p>
          <p onClick={   this.DisparaClick }>
            { Nome } 
          </p>
          <a
            onClick = {this.handleAClick}
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
