//import logo from './logo.svg';
import './App.css';
//import { render } from '@testing-library/react';

//só pra exemplificar duas maneiras de import
import { Component } from 'react';
//import React from 'react';
//class App extends React.Component{
class App extends Component{
  state = {
    name:'Clodoaldo Pereira',
    counter: 0,
    posts:[
      {
        id: 1,
        title: 'O título 01',
        body: 'O  corpo 1'
      },
      {
        id: 2,
        title: 'O título 02',
        body: 'O  corpo dois'
      },
      {
        id: 3,
        title: 'O título 03',
        body: 'O  corpo tres'
      }
    ]
  };

  componentDidMount(){
    this.loadPost();
  }

  loadPost = async()=>{
    const postResponse = fetch('https://jsonplaceholder.typicode.com/posts');
    const [posts] = await Promise.all([postResponse]);
    const postsJson = await posts.json();

    const photosResponse = fetch('https://jsonplaceholder.typicode.com/photos');
    const [photos] = await Promise.all([photosResponse]);
    const photosJson = await photos.json();

    const postsAndPhotos = postsJson.map((post, index) => {
      return { ...post, cover: photosJson[index].url}
    })

    this.setState({posts: postsAndPhotos});
  }

  handleTimeout = () =>{
    const { posts, counter } = this.state;
    posts[0].title = 'O título mudou em: ' + counter.toString()
    this.setState({posts, counter: counter + 1})
    setTimeout(() => {
      this.setState({posts, counter: counter + 1})
      console.log('setTimeOut oi...')  
    }, 3000);    
    console.log('Teste oi...')
  }

  render(){
    const { posts } = this.state;

    return (
      <section className="container">
        <div className="posts">
          <img src={post.cover} alt={post.title}/>;
          { posts.map(post => (
            <div className="post" key={post.id}>
              <div className="post-contente">
              <h1>{post.title}</h1>
              <p>{ post.body }</p>
              </div>          
            </div>
          ))}

        </div>
      </section>

    );

  }
}

export default App;
