import { Component } from 'react';
import { loadPosts } from '../../utils/load-posts';
import { Posts } from '../../components/Posts';
import { Button } from '../../components/Button';

//import React from 'react';
//class App extends React.Component{
class Home extends Component{
  state = {
    name:'Clodoaldo Pereira',
    counter: 0,
    allPosts:[],
    page:0,
    postsPerPage: 2,
    posts:[
      {
        id: 1,
        title: 'O título 01',
        body: 'O  corpo 1'
      },
      {
        id: 2,
        title: 'O título 02',
        body: 'O  corpo dois'
      },
      {
        id: 3,
        title: 'O título 03',
        body: 'O  corpo tres'
      }
    ]
  };

  async componentDidMount(){
    await this.loadPost();
    console.log('loadPost oi...') 
  }


  loadPost = async()=>{
    const{ page, postsPerPage } = this.state;
    const postsAndPhotos = await loadPosts();
    this.setState({ 
      posts: postsAndPhotos.slice(page, postsPerPage),
      allPosts: postsAndPhotos,
    });
  }

  handleTimeout = () =>{
    const { posts, counter } = this.state;
    posts[0].title = 'O título mudou em: ' + counter.toString()
    this.setState({posts, counter: counter + 1})
    setTimeout(() => {
      this.setState({posts, counter: counter + 1})
      console.log('setTimeOut oi...')  
    }, 3000);    
    console.log('Teste oi...')
  }

  loadMorePosts = () =>{    
    const{
      page,
      postsPerPage,
      allPosts,
      posts
    }=this.state;
    const nextPage= page + postsPerPage;
    const nextPost = allPosts.slice( nextPage, postsPerPage + nextPage );
    posts.push(...nextPost);
    this.setState({ posts, page: nextPage});
    console.log('loadMorePosts oi...',  nextPage, postsPerPage ) ;
  }

  render(){
    const { posts, page, postsPerPage, allPosts } = this.state;
    const disabled = page + postsPerPage >= allPosts.length;
    //console.log("Teste render ");
    return (
      <section className="container">
        <Posts  posts = {posts, disabled}  />
        <div className="button-container">
          <Button 
            text = { "Load More Posts" }
            onClick={ this.loadMorePosts}
          />
        </div>
      </section>

    );

  }
}

export default Home;
